mapper.select:
select a.simg,a.title,a.zans,a.adddate,a.artreads as "artreads",a.adddate,
a.scontent,a.labels,a.comments,a.aid,a.content
from article a where a.delflg=0 and a.aid = #{aid}

mapper.update:
update article set update = now()

	,title = #{title}


	,content = #{content}


	,delflg = #{delflg}

where aid = #{aid}
